import React, { useState } from 'react'
import Person from './Person'
import { motion } from 'framer-motion'
import '../css/style.css'
import { baseUrl } from './consts'

export default function PersonsList(props) {
	const [showDetails, setShowDetails] = useState(false)
	const [detailsFor, setDetailsFor] = useState(-1)

	const getById = async id => {
		const response = await fetch(`${baseUrl}/api/persons/${id}`)
		const data = await response.json()
		return data
	}

	const renderCards = () => {
		return (
			<div className="persons-container">
				{props.persons.map(person =>
					<motion.div
						key={person.id}
						initial={{ transform: 'scaleX(0)' }}
						animate={{ transform: 'scaleX(1)' }}
						transition={{ delay: 0.3 * person.id }}
					>
						<Person person={person} handleDetails={() => setShowDetails(true)} handleFor={x => setDetailsFor(x)} />
					</motion.div>
				)}
			</div>
		)
	}

	const renderDetails = async () => {
		// const { data } = await getById(detailsFor)
		const data = {
			avatar: 'smt',
			name: 'ceva',
			role: 'dev'
		}

		return (
			<div className="details-container" style={{ backgroundImage: `url(${data.avatar})` }}>
				<h2>{data.name}</h2>
				<h2>{data.role}</h2>
				<button onClick={() => setShowDetails(false)}>Close Details</button>
			</div>
		)
	}

	return !showDetails ? renderCards() : renderDetails()
}
